INSERT INTO tasks (id, account_id, title, description, status, "createdAt", "updatedAt", "deletedAt") VALUES
('clxtask1admin1234567890', 'clx1admin1234567890abcdef', 'プロジェクト計画書の作成', '来週のプロジェクト開始に向けて、詳細な計画書を作成する必要があります。スケジュール、リソース、リスク管理を含める。', 'TODO', NOW(), NOW(), NULL),
('clxtask2admin1234567890', 'clx1admin1234567890abcdef', '会議資料の準備', '月曜日のクライアント会議で使用するプレゼンテーション資料を準備する。最新の売上データとグラフを含める。', 'DONE', NOW(), NOW(), NULL),
('clxtask3admin1234567890', 'clx1admin1234567890abcdef', 'データベースの最適化', 'パフォーマンス向上のため、データベースクエリの最適化を行う。インデックスの見直しと不要なデータの削除を実施。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask4admin1234567890', 'clx1admin1234567890abcdef', 'コードレビューの実施', 'チームメンバーのプルリクエストをレビューし、フィードバックを提供する。セキュリティとパフォーマンスに重点を置く。', 'TODO', NOW(), NOW(), NULL),
('clxtask5admin1234567890', 'clx1admin1234567890abcdef', 'テストケースの追加', '新機能に対する包括的なテストケースを作成し、自動テストに追加する。エッジケースも含める。', 'DONE', NOW(), NOW(), NULL),
('clxtask6admin1234567890', 'clx1admin1234567890abcdef', 'システム監視ダッシュボードの構築', 'リアルタイムでシステムの状態を監視できるダッシュボードを構築する。アラート機能とログ分析機能も含める。', 'TODO', NOW(), NOW(), NULL),
('clxtask7admin1234567890', 'clx1admin1234567890abcdef', 'セキュリティポリシーの更新', '最新のセキュリティ脅威に対応するため、社内セキュリティポリシーを更新する。従業員向けの教育資料も作成する。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask8admin1234567890', 'clx1admin1234567890abcdef', 'API文書の自動生成', 'OpenAPI仕様に基づいてAPI文書を自動生成するシステムを構築する。開発者向けのインタラクティブな文書も含める。', 'DONE', NOW(), NOW(), NULL),
('clxtask9admin1234567890', 'clx1admin1234567890abcdef', 'マイクロサービス間の通信最適化', 'サービス間の通信を最適化し、レイテンシを削減する。メッセージキューイングとキャッシュ戦略を実装する。', 'TODO', NOW(), NOW(), NULL),
('clxtask10admin1234567890', 'clx1admin1234567890abcdef', '災害復旧計画の策定', 'システム障害や自然災害に備えた包括的な災害復旧計画を策定する。バックアップ戦略と復旧手順を文書化する。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask11admin1234567890', 'clx1admin1234567890abcdef', 'ユーザー認証システムの強化', '多要素認証とシングルサインオンを実装し、セキュリティを強化する。OAuth2とSAMLの統合も含める。', 'DONE', NOW(), NOW(), NULL),
('clxtask12admin1234567890', 'clx1admin1234567890abcdef', 'CI/CDパイプラインの改善', 'デプロイメントプロセスを自動化し、品質を向上させる。自動テストとロールバック機能を強化する。', 'TODO', NOW(), NOW(), NULL),
('clxtask13admin1234567890', 'clx1admin1234567890abcdef', 'データ分析基盤の構築', 'ビッグデータを効率的に処理できる分析基盤を構築する。機械学習パイプラインと可視化ツールも含める。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask14admin1234567890', 'clx1admin1234567890abcdef', 'コンプライアンス監査の実施', 'GDPRやSOXなどの規制要件に準拠しているか監査を実施する。改善点の特定と対応計画の策定を行う。', 'DONE', NOW(), NOW(), NULL),
('clxtask15admin1234567890', 'clx1admin1234567890abcdef', 'モバイルアプリの最適化', 'モバイルアプリのパフォーマンスを最適化し、ユーザー体験を向上させる。オフライン機能とプッシュ通知も実装する。', 'TODO', NOW(), NOW(), NULL),
('clxtask16admin1234567890', 'clx1admin1234567890abcdef', 'クラウド移行戦略の策定', 'オンプレミスからクラウドへの移行戦略を策定する。コスト分析とリスク評価も含める。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask17admin1234567890', 'clx1admin1234567890abcdef', '開発チームのスキルアップ', '最新技術に関する研修を企画し、チームのスキルレベルを向上させる。外部講師の招聘とハンズオンセッションも含める。', 'DONE', NOW(), NOW(), NULL),
('clxtask18admin1234567890', 'clx1admin1234567890abcdef', 'ログ管理システムの構築', '分散システムのログを統合管理するシステムを構築する。検索機能とアラート機能も実装する。', 'TODO', NOW(), NOW(), NULL),
('clxtask19admin1234567890', 'clx1admin1234567890abcdef', 'パフォーマンステストの実施', '高負荷時のシステム動作を検証し、ボトルネックを特定する。スケーリング戦略の策定も含める。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask20admin1234567890', 'clx1admin1234567890abcdef', 'ユーザーフィードバック分析', 'ユーザーからのフィードバックを分析し、改善点を特定する。優先度の高い要望を開発計画に反映する。', 'DONE', NOW(), NOW(), NULL),
('clxtask21admin1234567890', 'clx1admin1234567890abcdef', 'API設計の標準化', 'RESTful APIの設計ガイドラインを策定し、一貫性を保つ。バージョニング戦略とエラーハンドリングも含める。', 'TODO', NOW(), NOW(), NULL),
('clxtask22admin1234567890', 'clx1admin1234567890abcdef', 'セキュリティ監査の実施', 'アプリケーションのセキュリティ脆弱性をチェックし、修正を行う。外部セキュリティ会社との連携も検討する。', 'IN_PROGRESS', NOW(), NOW(), NULL),
('clxtask23admin1234567890', 'clx1admin1234567890abcdef', '自動テストの拡充', 'ユニットテスト、統合テスト、E2Eテストを拡充し、品質を向上させる。テストカバレッジの向上も目指す。', 'DONE', NOW(), NOW(), NULL),
('clxtask24admin1234567890', 'clx1admin1234567890abcdef', 'インフラ監視の強化', 'サーバーリソースとアプリケーションの監視を強化する。予測的メンテナンスの実現も目指す。', 'TODO', NOW(), NOW(), NULL),
('clxtask25admin1234567890', 'clx1admin1234567890abcdef', 'データガバナンスの確立', 'データの品質管理とプライバシー保護のためのガバナンス体制を確立する。データカタログの構築も含める。', 'IN_PROGRESS', NOW(), NOW(), NULL);
